import React, { Component } from 'react'

export default class TodoMain extends Component {
  state = {
    //当前双击的id
    currentId: '',
  }
  render() {
    const { list } = this.props
    return (
      <section className="main">
        <input id="toggle-all" className="toggle-all" type="checkbox" />
        <label for="toggle-all">Mark all as complete</label>
        <ul className="todo-list">
          {list.map((item) => {
            return (
              <li
                className={item.id === this.state.currentId ? 'editing' : ''}
                key={item.id}
              >
                <div className="view">
                  <input
                    className="toggle"
                    type="checkbox"
                    checked={item.done}
                    onChange={() => this.updateDone(item.id)}
                  />
                  <label onDoubleClick={() => this.showEdit(item.id)}>
                    {item.name}
                  </label>
                  <button
                    className="destroy"
                    onClick={() => this.delTodo(item.id)}
                  ></button>
                </div>
                <input
                  className="edit"
                  value="Create a TodoMVC template"
                  onChange={() => {}}
                />
              </li>
            )
          })}
        </ul>
      </section>
    )
  }
  //删除任务
  delTodo = (id) => {
    console.log('id', id)
    this.props.delTodoById(id)
    this.setState({
      currentId: id,
    })
  }
  //修改任务状态
  updateDone = (id) => {
    this.props.updateDoneById(id)
  }
  showEdit = (id) => {
    console.log('id', id)
  }
}
