import React, { Component } from 'react'

export default class Child extends Component {
    render() {
        return (
            <div>
                <h3>我是子组件</h3>
                <div>金钱：{this.props.money}</div>
                <button onClick={this.buy}>买衣服</button>
            </div>
        )
    }

    buy = () => {
        this.props.buy(199)
    }
}
